'use client';

import { motion, useInView, useAnimation, Variant } from 'framer-motion';
import { useEffect, useRef } from 'react';

type AnimatedTextProps = {
  text: string;
  className?: string; // User can override font etc.
  delay?: number; // Optional manual delay
};

export const Glitch = ({ text, className = '', delay = 0 }: AnimatedTextProps) => {
  const controls = useAnimation();
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true });

  useEffect(() => {
    if (isInView) {
      setTimeout(() => {
        controls.start('visible');
      }, delay * 1000);
    }
  }, [isInView, controls, delay]);

  return (
    <motion.div
      ref={ref}
      initial="hidden"
      animate={controls}
      variants={{
        visible: { transition: { staggerChildren: 0.01 } },
        hidden: {},
      }}
      className={className}
    >
      {text.split(/(\s+)/).map((word, wIndex) => (
        <span key={wIndex} className="inline-block">
          {word.split('').map((char, cIndex) => (
            <motion.span
              key={`${wIndex}-${cIndex}`}
              variants={{
                hidden: { opacity: 0, x: -10, scale: 1 },
                visible: {
                  opacity: 1,
                  x: [0, -2, 2, -1, 0],
                  scale: 1,
                  transition: { duration: 0.2, times: [0, 0.2, 0.5, 0.8, 1] }
                },
              }}
              className="inline-block"
            >
              {char}
            </motion.span>
          ))}
        </span>
      ))}
    </motion.div>
  );
};
